targets.expr:
sum by (pod) (
  rate(container_cpu_usage_seconds_total{pod=~".*aibrix.*", container!="", container!="POD"}[5m])
)
/
sum by (pod) (
  kube_pod_container_resource_limits{pod=~".*aibrix.*", resource="cpu", unit="core"}
)

sum by (pod) (
  container_memory_working_set_bytes{pod=~".*aibrix.*", container!="", container!="POD"}
)
/
sum by (pod) (
  kube_pod_container_resource_limits{pod=~".*aibrix.*", resource="memory", unit="byte"}
)
